// increment attempts and stop if too many retries
const it = items[0];
it.json.attempts = (it.json.attempts || 0) + 1;
if (it.json.attempts > 20) {
  throw new Error('Max polling attempts reached');
}
return it;

const item = items[0];
const gens = item.json.generations || [];
if (!gens.length) throw new Error('No generations in response');

const gen = gens[0]; // take first generation
const imgVal = gen.img || '';
const mime = gen.mime_type || 'image/png';

if (imgVal.startsWith('http')) {
  // hosted image URL
  item.json.imageUrl = imgVal;
  return [item];
} else {
  // base64 data -> create binary
  const buffer = Buffer.from(imgVal, 'base64');
  item.binary = item.binary || {};
  item.binary.image = {
    data: buffer,
    mimeType: mime,
    fileName: 'stablehorde.png',
  };
  return [item];
}

