let raw = $input.first().json.output || '';
// remove ```html and ``` markers
raw = raw.replace(/```html/, '').replace(/```/, '').trim();
return [{ json: { output_html: raw } }];

// Assume the HTML string is in `item.html`
const htmlContent = $input.first().json.generated_html; 
return {
  json: {},
  binary: {
    data: {
      data: Buffer.from(htmlContent).toString('base64'),
      mimeType: 'text/html',
      fileName: 'landing-page.html',
    }
  }
};
